import React from 'react';
import Square from './Square'
class Board extends React.Component {
  constructor(){
    super()
        this.state={
        t0:'',
        t1:'',
        t2:'',
        t3:'',
        t4:'',
        t5:'',
        t6:'',
        t7:'',
        t8:'',
        }
    }
    fun=(val)=>{
            if(val===0){this.setState(prevState=>{{t0:"X"}})}
            if(val===1){this.setState({t1:"X"})}
            if(val===2){this.setState({t2:"X"})}
            if(val===3){this.setState({t3:"X"})}
            if(val===4){this.setState({t4:"X"})}
            if(val===5){this.setState({t5:"X"})}
            if(val===6){this.setState({t6:"X"})}
            if(val===7){this.setState({t7:"X"})}
            if(val===8){this.setState({t8:"X"})}
            console.log(this.state)
    }


  renderSquare(i) {
    return <Square value={i} fun={this.fun} />;
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}
export default Board;
