import React from 'react';
import Square from './Square'
import update from 'react-addons-update';
class Board extends React.Component {
  constructor(){
    super()
        this.state={
        arr:['','','','','','','','',''],
        count:0
        }
    }
    fun=(val)=>{
    if(this.state.count%2===0){
            this.setState(update(this.state,{arr:{[val]:{$set:"X"}}}),()=>console.log(this.state))
            this.setState({count:this.state.count+1})
            }else{
            this.setState(update(this.state,{arr:{[val]:{$set:"O"}}}),()=>console.log(this.state))
             this.setState({count:this.state.count+1})
            }

    }


  renderSquare(i) {
    return <Square value={i} fun={this.fun} kill={this.state.arr[i]}/>;
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}
export default Board;
