import React from 'react';
import Square from './Square'
class Board extends React.Component {
  constructor(){
    super()
        this.state={
        t0:'',
        t1:'',
        t2:'',
        t3:'',
        t4:'',
        t5:'',
        t6:'',
        t7:'',
        t8:'',
        }
    }
    fun=(val)=>{
            if(val===0){this.setState({t0:"X"})}

    }


  renderSquare(i) {
    return <Square value={i} fun={this.fun} kill={this.state.t0}/>;
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}
export default Board;
