import React from 'react';
import Square from './Square'
class Board extends React.Component {
  constructor(){
    super()
        this.state={
        t0:'',
        t1:'',
        t2:'',
        t3:'',
        t4:'',
        t5:'',
        t6:'',
        t7:'',
        t8:'',
        count:0
        }
    }
    fun=(val)=>{
    if(this.state.count%2===0){
            if(val===0){this.setState({t0:"X"},()=>console.log(this.state))}
            if(val===1){this.setState({t1:"X"},()=>console.log(this.state))}
            if(val===2){this.setState({t2:"X"},()=>console.log(this.state))}
            if(val===3){this.setState({t3:"X"},()=>console.log(this.state))}
            if(val===4){this.setState({t4:"X"},()=>console.log(this.state))}
            if(val===5){this.setState({t5:"X"},()=>console.log(this.state))}
            if(val===6){this.setState({t6:"X"},()=>console.log(this.state))}
            if(val===7){this.setState({t7:"X"},()=>console.log(this.state))}
            if(val===8){this.setState({t8:"X"},()=>console.log(this.state))}
            this.setState({count:this.state.count+1})
            }else{
             if(val===0){this.setState({t0:"O"},()=>console.log(this.state))}
            if(val===1){this.setState({t1:"0"},()=>console.log(this.state))}
            if(val===2){this.setState({t2:"0"},()=>console.log(this.state))}
            if(val===3){this.setState({t3:"0"},()=>console.log(this.state))}
            if(val===4){this.setState({t4:"0"},()=>console.log(this.state))}
            if(val===5){this.setState({t5:"0"},()=>console.log(this.state))}
            if(val===6){this.setState({t6:"0"},()=>console.log(this.state))}
            if(val===7){this.setState({t7:"0"},()=>console.log(this.state))}
            if(val===8){this.setState({t8:"0"},()=>console.log(this.state))}
            }

    }


  renderSquare(i) {
    return <Square value={i} fun={this.fun} kill={`${this.state.t+i}`}/>;
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}
export default Board;
